sys_script_include.script:
var DXC_CreateNewDXCRequest_CheckURL = Class.create();
DXC_CreateNewDXCRequest_CheckURL.prototype = {
	initialize: function() {
	},

	IsURLExists:function(URL)
	{
		/*
		try
		{			
		}
		catch(ex)
		{
			gs.addErrorMessage( ex.getMessage());
		}*/
		var request= new sn_ws.RESTMessageV2();
		request.setEndpoint(URL);
		request.setHttpTimeout(60);
		request.setHttpMethod("get");
		var response = request.executeAsync();
		response.haveError();		
		var status = response.getStatusCode();
		return status;
	},
	type: 'DXC_CreateNewDXCRequest_CheckURL'
};
