sys_script_include.script:
var DXC_AllModule_AccessField = Class.create();
DXC_AllModule_AccessField.prototype ={
	initialize :function(){},
	IsDXC_AllModule_AccessField: function(mess) {
		var answer=false;
		//var LogMess=this.getParameter("sysparm_mess");		
		var state= current.state.toString();
		var IsOwn=gs.getUserID()==current.submitted_by;
		if(gs.hasRole("x_229611_dxc_docs.DXC Admin"))
		{
			gs.info("ACL1 DXC "+mess);
			answer = true;
		}
		else if(gs.hasRole("x_229611_dxc_docs.DXC Employee")&&IsOwn)
		{
			gs.info("ACL2 DXC "+mess);
			var checkState=state==="Declined"||state==="Cancelled"||state==="Approved"||state==="WaitingPMApprover"||state==="WaitingLeader";
			//state==="Declined"||state==="Cancelled"||state==="Approved"
			if(checkState)
			{
				gs.info("ACL3 DXC "+mess);
				answer=false;
			}
			else
			{
				gs.info("ACL4 DXC "+mess);
				answer=true;
			}
		}
		else
		{
			gs.info("ACL5 DXC "+mess);
			answer=false;
		}
		return answer;
	},

	type: 'DXC_AllModule_AccessField'
};
