sys_security_acl.script:
var state= current.state.toString();
var IsOwn=gs.getUserID()==current.submitted_by;
if(gs.hasRole("x_229611_dxc_docs.DXC Admin"))
{
	gs.info("ACL1 DXC SubCategory");
	answer = true;
}
else if(gs.hasRole("x_229611_dxc_docs.DXC Employee")&&IsOwn)
{
	gs.info("ACL2 DXC SubCategory");
	if(state==="Declined"||state==="Cancelled")
	{
		gs.info("ACL3 DXC SubCategory");
		answer=false;
	}
	else
	{
		gs.info("ACL4 DXC SubCategory");
		answer=true;
	}	
}
else
{
	gs.info("ACL5 DXC SubCategory");
	answer=false;
}
